import classes from "./Article.module.css";
import { Link, useHistory } from "react-router-dom";
import { useContext, useEffect, useState } from "react";
import AuthContext from "../store/auth-context";
import axios from "axios";
import AllArticles from "../Pages/AllArticles";

const Article = (props) => {
  const authCtx = useContext(AuthContext);
  const history = useHistory();
  const [isArticle, setIsArticle] = useState(false);
  const [deleted, setDeleted] = useState(false);

  const deleteHandler = () => {
    axios
    .delete("http://localhost:3001/articles/"+`${props.id}`, { withCredentials: true })
    .then((response) => {
      if (response.data.status === 200) {
        console.log(response.data.staus)
        setDeleted(!deleted);
        history.replace("/articles")
      }
    })
    .catch((error) => {
      console.log("error : ", error);
    });
  }

  const [articles, setArticles] = useState([]);
  const showArticleHandler = () => {
    axios
      .get("http://localhost:3001/articles", { withCredentials: true })
      .then((response) => {
        if (response.data.status === "success") {
          setArticles(response.data.articles);
          if (response.data.articles) {
            setIsArticle(true);
          }
        }
      })
      .catch((error) => {
        console.log("error : ", error);
      });
  };

  useEffect(() => {
    showArticleHandler();
    setDeleted(false);

  }, []);


  return (
    <li className={classes.item}>
      <figure>
        <blockquote>
          <p>{props.title}</p>
        </blockquote>
        <figcaption>{props.description}</figcaption>
      </figure>
      <Link className="btn" to={`/articles/${props.id}`}>
        View
      </Link>{" "}
      &nbsp;
      {/* { authCtx.id === props.user_id && ( */}
      <Link
        className="btn"
        to={{
          pathname: `/articles/${props.id}/edit`,
          params: { title: props.title, desc: props.description },
        }}
      >
        Edit
      </Link>{" "}
      &nbsp;
      {/* )} */}
      <button onClick={deleteHandler} className="btn btn-red">Delete</button>
    </li>
  );
};

export default Article;
