import classes from './Article.module.css';
import { Link } from 'react-router-dom';
import { useContext } from "react";
import AuthContext from "../store/auth-context";

const Article = (props) => {
  const authCtx = useContext(AuthContext);
  console.log(props)
  return (
    <li className={classes.item}>
      <figure>
        <blockquote>
          <p>{props.title}</p>
        </blockquote>
        <figcaption>{props.description}</figcaption>
      </figure>
      <Link className='btn' to={`/articles/${props.id}`}>
        View
      </Link> &nbsp;
      {/* { authCtx.id === props.user_id && ( */}
      <Link className='btn'  to={{ pathname: `/articles/${props.id}/edit`, params: { title: props.title, desc: props.description }}}>
        Edit
      </Link> 
      {/* )} */}
      &nbsp;
      <Link className='btn btn-red' to={`/articles/${props.id}`}>
        Delete
      </Link>
    </li>
  );
};

export default Article;
