const ArticleDetail = (props) => {
  return (
  <Card className={classes.modal}>
    <header className={classes.header}>
      <h2>{props.title}</h2>
    </header>
    <div className={classes.content}>
      <p>{props.description}</p>
    </div>
    <footer className={classes.actions}>
      <button>Okay</button>
    </footer>

  </Card>)
};

export default ArticleDetail;
