import React, { useEffect, useState } from "react";
import axios from "axios";
import Articles from "../Articles/Articles";
import ArticleDetail from "./ArticleDetail";

const AllArticles = () => {
  const [isArticle, setIsArticle] = useState(false);
  const [articles, setArticles] = useState([]);
  const [open, setOpen] = useState(false);

  const openHandler = () => {
    setOpen(true);
  };

  const closeHandler = () => {
    setOpen(false);
  };

  const showArticleHandler = () => {
    axios
      .get("http://localhost:3001/articles", { withCredentials: true })
      .then((response) => {
        if (response.data.status === "success") {
          setArticles(response.data.articles);
          if (response.data.articles) {
            setIsArticle(true);
          }
        }
      })
      .catch((error) => {
        console.log("error : ", error);
      });
  };

  useEffect(() => {
    showArticleHandler();
  }, []);

  return (
    <div>   
      {open && (
        <ArticleDetail
          onConfirm={closeHandler}
          title="hello" //{props.title}
          description="hii there!!"//{props.description}
        />
      )}   
      <Articles articles={articles} onShow={showArticleHandler} onOpen={openHandler} />
      {articles.length === 0 && <p className='centered'>No Article. please add from "create" tab.</p>}

    </div>
  );
};

export default AllArticles;
